const SearchBar = ({ setSearchTerm }) => {

    const [state, setstate] = useState('');

    return(
        <Wrapper>
            <Content>
                <img src={searchIcon} alt = 'search-icon' />

                <input 
                    type='text'
                    placeholder = 'Search Movie'
                    
                />

            </Content>
        </Wrapper>
    )
}

export default SearchBar;
