import styled from "styled-components";

export const Wrapper = styled.div`

    display: flex;
    align-items: center;
    height: 100px;
    background: var(--darkGrey);
    padding: 0 20px;

`;

export const Content = styled.div`

    position: relative;
    max-width: var(--maxWidth);
    width: 100%;
    height: 50px;
    background: var(--medGrey);
    margin: 0 auto;
    border-radius: 40px;
    color: var(--white);

    img{
        position: absolute;
        left: 13px;
        top: 13px;
        width: 25px;
    }

    input{
        font-size: var(--fontBig);
        position: absolute;
        left: 0;
        margin: 7px 0;
        padding: 0 0 0 55px;
        border: 0;
        width: 95%;
        background: transparent;
        height: 40px;
        color: var(--white);

        :focus{
            outline: none;
        }
    }

` ;
